import MyButton from './components/MyButton';
function App() {
  const [inputValue, setInputValue] = useState('');

  const handleChange = (event) => {
    const value = event.target.value;
    setInputValue(value);
    document.body.style.backgroundColor = value.toLowerCase() === 'grey' ? 'grey' : '';
  };
  return (
    <div className="App">
      <MyButton />
    </div>
  );
}

export default App;
